import React from 'react';  


export default function StraightLine(props: any) {

    // let principal = Number(200000);
    // let interestRate = Number(5/100);
    // let tenor = Number(12/12);

    let principal = parseFloat(props.amount);
    let interestRate = parseFloat(props.rate)/100;
    let tenor = parseFloat(props.tenor);


    let amount;
    let simpleInt;
    let schedule;

    simpleInt = (principal * interestRate * tenor);
    amount = principal + simpleInt;

    function simpleInterest() {

        schedule = "<table> \
			<tr><td><b>S/N</b></td><td><b>Principal</b></td>\
			<td><b>Interest</b></td><td><b>Repayment</b></td></tr> \
			 \
            ";

        // let monthlyPrincipal = principal/12;
        // let monthlyInterestRate = interestRate/12;
        // let monthlyInterest = principal*monthlyInterestRate;


        let monthlyPrincipal = principal/tenor;
        let monthlyInterestRate = interestRate/tenor;
        let monthlyInterest = principal*monthlyInterestRate;

        let monthlyRepayment = monthlyPrincipal + monthlyInterest;

        let i =1;
        while (i <= tenor) {

            schedule += "<tr><td>"+i+"</td><td>"+monthlyPrincipal+"</td> \
            <td>"+monthlyInterest+"</td> \
            <td>"+monthlyRepayment+"</td> \
            </tr>";

            i++;
        }
        schedule += "</table>";

        return schedule;

    }


    return (
        <>  
            <div><h3>Straight line Method </h3></div>
            <div>Interest: ₦{simpleInt}</div>
            <div>Total plus interest: ₦{amount}</div>

            <div><h3>Schedule</h3></div>
        {
            simpleInterest()
        }

        </>
    ) 
}
