import React, { Fragment } from "react";
import { useTable } from "react-table";
import ReactDatatable from "@ashvin27/react-datatable";
// import 'bootstrap/dist/css/bootstrap.css';

function LoanDetails() {
  const data = React.useMemo(
    () => [
      {
        col1: "AGl-123",
        col2: "Personal",
        col3: "N200,000",
        col4: "May 26, 2019",
        col5: "Decline",
        col6: "Open",
      },
      {
        col1: "AGl-124",
        col2: "Personal",
        col3: "N200,000",
        col4: "May 26, 2019",
        col5: "Decline",
        col6: "Open",
      },
      {
        col1: "AGl-145",
        col2: "Personal",
        col3: "N550,000",
        col4: "October 16, 2019",
        col5: "Decline",
        col6: "Open",
      },
      {
        col1: "AGl-943",
        col2: "Personal",
        col3: "N560,000",
        col4: "October 17, 2019",
        col5: "Decline",
        col6: "Open",
      },
      {
        col1: "AGl-234",
        col2: "Personal",
        col3: "N500,000",
        col4: "October 18, 2019",
        col5: "Decline",
        col6: "Closed",
      },
    ],
    []
  );

  const columns = React.useMemo(
    () => [
      {
        Header: "Loan ID",
        accessor: "col1", // accessor is the "key" in the data
      },
      {
        Header: "Type",
        accessor: "col2",
      },
      {
        Header: "Amount",
        accessor: "col3",
      },
      {
        Header: "Date",
        accessor: "col4",
      },
      {
        Header: "Status",
        accessor: "col5",
      },
      {
        Header: "Action",
        accessor: "col6",
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({ columns, data });

  return (
    <>
      <main className="md:pl-12">
        <div className="flex ">
          <div className="w-full ">
            <div className="flex items-center mt-10 lg:mt-0">
              <div className="w-8 h-1 bg-blue-700 rounded mr-0 hidden md:block"></div>
              <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                Loan Details
              </h4>
            </div>
            <br />
            <div className="w-11/12 bg-white flex flex-column rounded py-12 shadow-lg border-b-4 border-blue-700 md:border-none pl-4 mb-20 m-auto md:m-0">
              <div className="w-1/4 text-blue-600 font-bold text-4xl">
                <h2>AGl-1234</h2>
              </div>
              <div className="w-1/4">
                <div>
                  <p>Amount</p>
                  <p>N200,000</p>
                </div>
                <div>
                  <p>Duration</p>
                  <p>15 months</p>
                </div>
              </div>
              <div className="w-1/4">
                <p>Approved</p>
                <div>
                  <p>Repayment</p>
                  <p>N14,000</p>
                </div>
              </div>
              <div className="w-1/4">
                <button>Make Payment</button>
              </div>
            </div>
            <br />
            <br />
            <div className="flex items-center mt-10 lg:mt-0">
              <div className="w-8 h-1 bg-blue-700 rounded mr-0 hidden md:block"></div>
              <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                Repayment Log
              </h4>
            </div>
            <br />
            <div className="w-11/12 bg-white flex rounded py-12 shadow-lg border-b-4 border-blue-700 md:border-none pl-4 mb-20 m-auto md:m-0">
              <table {...getTableProps()} className="table-auto w-auto">
                <thead>
                  {headerGroups.map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                      {headerGroup.headers.map((column) => (
                        <th
                          {...column.getHeaderProps()}
                          className="px-4 py-3 text-xs font-semibold text-gray-700 border-b w-48"
                          //   style={{
                          //     borderBottom: "solid 3px red",
                          //     background: "aliceblue",
                          //     color: "black",
                          //     fontWeight: "bold",
                          //   }}
                        >
                          {column.render("Header")}
                        </th>
                      ))}
                    </tr>
                  ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                  {rows.map((row) => {
                    prepareRow(row);
                    return (
                      <tr {...row.getRowProps()}>
                        {row.cells.map((cell) => {
                          return (
                            <td
                              {...cell.getCellProps()}
                              style={{
                                padding: "10px",
                                fontSize: 16,
                                textAlign: "center",
                                paddingBottom: 40,
                              }}
                              className="border-b "
                            >
                              {cell.render("Cell")}
                            </td>
                          );
                        })}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
            <div className="w-11/12 mt-8 hidden md:block">
              <hr className="w-9/12 border-t-2 border-blue-600 bg-blue-400 rounded m-auto"></hr>
            </div>
          </div>
        </div>
        <br />
      </main>
    </>
  );
}

export default LoanDetails;
