import React, { useEffect, useCallback } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Keycloak from "keycloak-js";

import Layout from "./components/Layout";
import Home from "./pages/Home";
import Profile from "./pages/Profile";
import Onboarding from "./pages/Onboarding";
import Bvn from "./pages/Bvn";
import useAuthStore from "./stores/authStore";
import useProfileStore from "./stores/profileStore";
import MyLoans from "./pages/MyLoans";
import NewLoanRequest from "./pages/NewLoanRequest";
import LoanSchedule from "./pages/LoanSchedule";
import LoanApplication from "./pages/LoanApplication";

import { getPersonalDetails } from "./clients/profile";
import { getProfileSection } from "./clients/onboarding";

export default function App() {
  const authenticated = useAuthStore(
    useCallback((state) => state.authenticated, [])
  );

  const initializeAuthStore = useAuthStore(
    useCallback((state) => state.initialize, [])
  );

  const initializeProfileStore = useProfileStore(
    useCallback((state) => state.initialize, [])
  );

  const initializeProfileSection = useProfileStore(
    useCallback((state) => state.initializeProfileSection, [])
  );

  const _initializeAuthentication = useCallback(() => {
    const keycloak = Keycloak({
      realm: process.env.REACT_APP_AUTH_REALM ?? "",
      url: process.env.REACT_APP_AUTH_SERVER_URL ?? "",
      clientId: process.env.REACT_APP_AUTH_CLIENT_ID ?? "",
    });

    keycloak.init({ onLoad: "login-required" }).then(async (authenticated) => {
      initializeAuthStore(keycloak);
      const { token } = keycloak;
      try {
        const { data } = await getPersonalDetails(token);
        const checklist = [
          {
            label: "Personal Information",
            name: "personal",
            sequence: 1,
            completed: true,
          },
          {
            label: "Next of Kin Information",
            name: "next_of_kin",
            sequence: 2,
            completed: false,
          },
          {
            label: "Contact Information",
            name: "contact",
            sequence: 3,
            completed: false,
          },
          {
            label: "Education",
            name: "education",
            sequence: 4,
            completed: true,
          },
          {
            label: "Employment",
            name: "employment",
            sequence: 5,
            completed: false,
          },
          {
            label: "Account Details",
            name: "account",
            sequence: 6,
            completed: true,
          },
        ];
        initializeProfileStore(data);
        initializeProfileSection(
          checklist.reverse(),
          checklist.every((loan) => loan.completed === true)
        );
        console.log(checklist);
      } catch (error) {}
    });
  }, [initializeAuthStore]);

  useEffect(() => {
    _initializeAuthentication();
    return () => {};
  }, [_initializeAuthentication]);

  if (!authenticated) {
    return <div>One moment...</div>;
  }

  return (
    <Router>
      <Layout>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/new-loan-request">
            <NewLoanRequest />
          </Route>
          <Route path="/loan-schedule">
            <LoanSchedule />
          </Route>
          <Route path="/profile">
            <Profile />
          </Route>
          <Route path="/loans">
            <MyLoans />
          </Route>
          <Route path="/onboarding">
            <Onboarding />
          </Route>
          <Route path="/application">
            <LoanApplication />
          </Route>
        </Switch>
      </Layout>
    </Router>
  );
}
