import React, { Fragment } from "react";
import { useTable } from "react-table";
import ReactDatatable from "@ashvin27/react-datatable";
import { useParams } from "react-router-dom";
import { getLoan } from "../clients/loan";
import { useQuery } from "react-query";
import useAuthStore from "../stores/authStore";
import { withRouter } from "react-router";

// import 'bootstrap/dist/css/bootstrap.css';

function LoanDetails(props) {
  const token = useAuthStore((state) => state.accessToken);

  let { loanId } = useParams();
  const { isLoading, error, data: loan } = useQuery("loanDetail", () =>
    getLoan(token, loanId)
  );

  let loanData = loan && loan.data;
  let loanDatas = [] as any;

  loan &&
    loanData["schedules"].map((item) => {
      loanDatas.push({
        col1: item.id,
        col2: item.repayment,
        col3: item.loan_amount,
        col4: item.repayment_starting_date,
      });
    });
  // if (loan) {
  //   loanData = loan.data;
  // }
  const data = React.useMemo(
    () => [
      // {
      // col1: "1",
      // col2: "Personal",
      // col3: "N200,000",
      // col4: "May 26, 2019",
      // col5: "Decline",
      // col6: "Open",
      // },
      // {
      // col1: "2",
      // col2: "Personal",
      // col3: "N200,000",
      // col4: "May 26, 2019",
      // col5: "Decline",
      // col6: "Open",
      // },
      // {
      // col1: "3",
      // col2: "Personal",
      // col3: "N550,000",
      // col4: "October 16, 2019",
      // col5: "Decline",
      // col6: "Open",
      // },
      // {
      // col1: "4",
      // col2: "Personal",
      // col3: "N560,000",
      // col4: "October 17, 2019",
      // col5: "Decline",
      // col6: "Open",
      // },
      // {
      //   col1: "5",
      // col2: "Personal",
      // col3: "N500,000",
      // col4: "October 18, 2019",
      // col5: "Decline",
      // col6: "Closed",
      // },
    ],
    []
  );

  const columns = React.useMemo(
    () => [
      {
        Header: "S/N",
        accessor: "col1", // accessor is the "key" in the data
      },
      {
        Header: "Amount",
        accessor: "col2",
      },
      {
        Header: "Date",
        accessor: "col3",
      },
      {
        Header: "Status",
        accessor: "col4",
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({ columns, data });

  if (isLoading) {
    return (
      <>
        <div className="w-full m-auto px-8 relative">
          <img
            src="/img/loading-gif.gif"
            alt=""
            className="w-1/8 m-auto py-20"
          />
        </div>
      </>
    );
  }

  return (
    <>
      <main className="md:pl-12">
        <div className="flex ">
          <div className="w-full ">
            <div className="flex items-center mt-10 lg:mt-0">
              <div className="w-8 h-1 bg-blue-700 rounded mr-0 hidden md:block"></div>
              <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                Loan Details
              </h4>
            </div>
            <br />
            <div className="w-11/12 bg-white flex flex-row rounded py-6 shadow-lg border-b-4 border-blue-700 md:border-none pl-4 mb-20 m-auto md:m-0">
              <div className="w-1/4  border-r-8 border-blue-100 align-middle justify-center">
                <h2 className="text-blue-600 text-4xl font-semibold py-5 pl-3">
                  {loanData.loan_id}
                </h2>
              </div>
              <div className="w-1/4 flex-row">
                <div className="flex flex-row text-blue-600 text-lg mb-4">
                  <div className="w-1/2 text-right mr-5">Amount: </div>
                  <div className="w-1/2 text-left font-bold">
                    N{loanData.loan_amount}
                  </div>
                </div>
                <div className="flex flex-row text-blue-600 text-lg">
                  <div className="w-1/2 text-right mr-5">Duration</div>
                  <div className="w-1/2 text-left font-bold">
                    {loanData.number_of_payments} months
                  </div>
                </div>
              </div>
              <div className="w-1/4 flex flex-col ">
                <div className="w-full mb-4">
                  <p
                    style={{ fontSize: 8 }}
                    className="text-center w-1/3 float-right text-teal-600 bg-teal-100 py-2  rounded-full"
                  >
                    {loanData.status}
                  </p>
                </div>

                <div className="flex flex-row text-blue-600 text-lg">
                  <p className="w-1/2 text-right mr-5">Repayment</p>
                  <p className="w-1/2 text-right font-bold"></p>
                </div>
              </div>
              <div
                style={{ alignItems: "center" }}
                className="w-1/4  flex justify-end align-middle "
              >
                <button
                  onClick={() => props.history.push("/pay/" + loanId)}
                  className="bg-green-700 text-white inline-flex -mr-6 px-6 py-3 rounded"
                >
                  <span className="text-lg">Make Payment</span>
                  <svg
                    className="mt-1"
                    width="14"
                    height="14"
                    viewBox="0 0 18 18"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <g id="chevron_right_24px">
                      <path
                        id="icon/navigation/chevron_right_24px"
                        d="M9.70492 6L8.29492 7.41L12.8749 12L8.29492 16.59L9.70492 18L15.7049 12L9.70492 6Z"
                        fill="white"
                      />
                    </g>
                  </svg>
                </button>
              </div>
            </div>
            <br />
            <br />
            <div className="flex items-center mt-10 lg:mt-0">
              <div className="w-8 h-1 bg-blue-700 rounded mr-0 hidden md:block"></div>
              <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                Repayment Log
              </h4>
            </div>
            <br />
            <div className="w-11/12 bg-white flex rounded py-12 shadow-lg border-b-4 border-blue-700 md:border-none pl-4 mb-20 m-auto md:m-0">
              <table {...getTableProps()} className="table-auto w-full">
                <thead>
                  {headerGroups.map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                      {headerGroup.headers.map((column) => (
                        <th
                          {...column.getHeaderProps()}
                          className="px-4 py-3 text-xs font-semibold text-gray-700 border-b w-48"
                          //   style={{
                          //     borderBottom: "solid 3px red",
                          //     background: "aliceblue",
                          //     color: "black",
                          //     fontWeight: "bold",
                          //   }}
                        >
                          {column.render("Header")}
                        </th>
                      ))}
                    </tr>
                  ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                  {rows.map((row) => {
                    prepareRow(row);
                    return (
                      <tr {...row.getRowProps()}>
                        {row.cells.map((cell) => {
                          return (
                            <td
                              {...cell.getCellProps()}
                              style={{
                                padding: "10px",
                                fontSize: 16,
                                textAlign: "center",
                                paddingBottom: 40,
                              }}
                              className="border-b "
                            >
                              {cell.render("Cell")}
                            </td>
                          );
                        })}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
            <div className="w-11/12 mt-8 hidden md:block">
              <hr className="w-9/12 border-t-2 border-blue-600 bg-blue-400 rounded m-auto"></hr>
            </div>
          </div>
        </div>
        <br />
      </main>
    </>
  );
}

