import React, { useCallback, useEffect } from "react";
import { NavLink, useHistory } from "react-router-dom";
import useAuthStore from "../stores/authStore";

function SideBar(props:any) {

  useEffect(()=>{
    console.log(props.navValue)
  }, [props.navValue])

  const history = useHistory();

  const logout = useAuthStore(useCallback((state) => state.logout, []));

  const _logout = () => {
    logout();
    history.replace("/");
  };

  return (
      <div className={props.navValue?"transition duration-150 ease-in-out w-8/12 fixed left-0 bg-white h-screen border-r z-40 pt-6" : "hidden lg:w-3/12 pt-12 border-r bg-white h-full relative lg:inline"}>
          <div className="flex justify-center mb-4 lg:mb-20">
              <img src="img/agile-credit-logo.png" className="w-24" alt="" />
          </div>
          <div className="m-auto w-9/12 mb-8 lg:mb-16">
              <button className="flex w-auto shadow-xl text-xl text-white px-8 lg:px-10 py-2 lg:py-3 items-center bg-blue-700 rounded-md hover:bg-blue-600">
                  <svg width="24" height="25" viewBox="0 0 24 25" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <g id="add_24px">
                        <path id="icon/content/add_24px" d="M19 13.3832H13V19.229H11V13.3832H5V11.4346H11V5.58878H13V11.4346H19V13.3832Z" fill="white"/>
                    </g>
                  </svg>
                  {/* <img src="img/plus-icon.svg" className="w-4 lg:w-6" alt="" /> */}
                  <span className="ml-2 text-lg font-normal">
                      Take a loan
                  </span>

              </button>
          </div>
          <div className="w-11/12 ml-auto lg:mb-24">
              <NavLink to="" className="flex bg-indigo-200 p-4 rounded-tl-lg rounded-bl-lg mb-1">
                  <img src="img/overview-icon.svg" className="ml-3" alt="" />
                  <span className="text-blue-700 ml-6 lg:text-sm xl:text-lg font-light">Overview</span>
              </NavLink>
              <NavLink to=""  className="flex p-4 rounded-tl-lg rounded-bl-lg mb-1">
                  <img src="img/loans-icon.svg" className="ml-3" alt="" />
                  <span className="text-gray-700 ml-6 lg:text-sm xl:text-lg font-light">My Loans</span>
              </NavLink>
              <NavLink to="" className="flex p-4 rounded-tl-lg rounded-bl-lg mb-1">
                  <img src="img/payment-icon.svg" className="ml-3" alt="" />
                  <span className="text-gray-700 ml-6 lg:text-sm xl:text-lg font-light">Make Payments</span>
              </NavLink>
              <NavLink to="" className="flex p-4 rounded-tl-lg rounded-bl-lg mb-1">
                  <img src="img/documents-icon.svg" className="ml-3" alt="" />
                  <span className="text-gray-700 ml-6 lg:text-sm xl:text-lg font-light">My Documents</span>
              </NavLink>
              <NavLink to="" className="flex p-4 rounded-tl-lg rounded-bl-lg mb-1">
                  <img src="img/payments-icon.svg" className="ml-3" alt="" />
                  <span className="text-gray-700 ml-6 lg:text-sm xl:text-lg font-light">Payment Methods</span>
              </NavLink>
          </div>
          <br />
          <div className=" bottom-0 border-t">
              <div className="w-11/12 ml-auto mt-8">
                  <NavLink to="" className="flex p-4 items-center rounded-tl-lg rounded-bl-lg mb-1">
                      <img src="img/web-icon.svg" className="ml-3 w-5 h-5" alt="" />
                      <span className="text-gray-700 ml-6 lg:text-sm xl:text-lg font-light">Visit Website</span>
                  </NavLink>
                  <button onClick={_logout} className="hidden lg:flex p-4 rounded-tl-lg rounded-bl-lg mb-1 focus:outline-none">
                      <img src="img/logout-icon.svg" className="ml-3 w-5 h-8" alt="" />
                      <span className="text-gray-700 ml-6 lg:text-sm xl:text-lg font-light">Logout</span>
                  </button>
                  <button onClick={_logout} className="lg:hidden text-red-600 flex p-4 rounded-tl-lg rounded-bl-lg mb-1 focus:outline-none">
                      <img src="img/logout-icon.svg" className="ml-3 w-5 h-6" alt="" />
                      <span className="text-red-600 ml-6 lg:text-sm xl:text-lg font-light">Logout</span>
                  </button>
              </div>
          </div>
          <br /><br /><br />
      </div>
    // <nav>
    //   <ul>
    //     <li>
    //       <NavLink to="/">Home</NavLink>
    //     </li>
    //     <li>
    //       <NavLink to="/profile">Profile</NavLink>
    //     </li>

    //     <button onClick={_logout}>Logout</button>
    //   </ul>
    // </nav>
  );
}

export default SideBar;
