import React from "react";
import useAuthStore from "../stores/authStore";

function Header(props:any) {
  const user :any = useAuthStore((state) => state.currentUser);
  console.log(user)
  return (
  <div>

    {/* desktop view */}
    <header className="hidden lg:flex lg:justify-between lg:bg-white lg:pt-3 lg:pb-3 lg:pl-12 lg:shadow-sm">
        <div className="">
            <h1 className="text-gray-700 text-md font-semibold pt-2">Agile Credit Loan Portal</h1>
        </div>
        <div className="flex items-center mr-16">
            <h4 className="text-gray-700 text-md font-light mr-5">Hello, {user && user.lastName}</h4>
            <img src="img/Notifications.png" alt="" />
        </div>

    </header>
    <br className="hidden lg:inline" /><br className="hidden lg:inline" />

    {/* mobile view */}
    <header className="flex justify-start pl-8 lg:hidden">
      <button className="h-12 w-12 focus:outline-none" onClick={()=>props.navBar()}>
          <img src="img/Hamburger Menu.png"  alt="" />
      </button>
      <img src="img/agile-credit-logo-large 1.png" className="w-16 ml-24" alt=""  />
    </header>
    <br className="lg:hidden" />
        <div className="w-full text-center mb-10 lg:hidden">
            <h1 className="text-green-500 font-semibold lg:hidden">Hello, {user && user.lastName}!</h1>
        </div>

  </div>
  );
}

export default Header;
