import React, {useEffect} from "react";
import { useQuery } from "react-query";
import { NavLink, useHistory } from "react-router-dom";
import { withRouter } from "react-router";

function Onboarding(props:any) {

  useEffect(()=>{
    console.log(props.navValue)
  }, [props.navValue])

  const history = useHistory();

  const goToBvn = ()=>{
    props.history.push('bvn')
  }

  return (
    <>

    {/* Desktop view */}
    <main className=" lg:block lg:pl-12">
      <div className="flex ">
          <div className="w-full ">
              <div className="flex items-center">
                  <div className="w-8 h-1 bg-blue-700 rounded mr-3"></div>
                  <h4 className="text-gray-700 font-semibold text-xl tracking-wide">Onboarding</h4>
              </div>
              <br />
              <div className="w-11/12 bg-white flex-none  rounded py-20 shadow-lg ">
                <div className="w-full m-auto mb-6">
                    <img src="img/checklists.svg" alt="" className="w-1/4 m-auto"/>
                </div>
                <div className="w-2/4 m-auto">
                    <p className="text-gray-700 font-medium text-center">Oops! Looks like you need to complete your profile before we can disburse your money.</p>
                </div>
                <div className="w-full m-auto mt-10">
                    <button onClick={()=> goToBvn()} className="flex w-1/4 shadow-xl text-xl font-light text-white justify-center pt-3 pb-3 items-center bg-green-600 hover:bg-green-700 rounded-md m-auto">
                    <span className="ml-4 text-sm xl:text-lg lg:text-sm pr-2">
                        Okay, let's go!
                    </span>
                    <img src="img/chevron-right.svg" className="w-5 pt-1" alt="" />
                    </button>
                </div>
              </div>
              <div className="w-11/12 mt-8">
                <hr className="w-9/12 border-t-2 border-blue-600 bg-blue-400 rounded m-auto"></hr>
              </div>
          </div>
      </div>
      <br />
    </main>

    {/* End of Desktop View */}
    </>
  );
}

export default withRouter(Onboarding);
