import React, { useState, useEffect } from "react";
import { withRouter } from "react-router";
import {getLoanApplications} from "../clients/loan";
import { getProducts } from "../clients/products";
import { useQuery } from "react-query";
import useAuthStore from "../stores/authStore";


const LoanRequests = () =>{


    const token = useAuthStore((state) => state.accessToken);

    const { isLoading, error, data } = useQuery("applications", () =>
        getLoanApplications(token)
    );

    const { isLoading : isProductLoading, error: productError, data: productData } = useQuery("products", () =>
        getProducts(token)
    );

    const getProductName = (productNo) =>{
        let products: any[] | null = productData?.data;

        if (!products) {
            return;
        }
        let currentProduct = products.find((prod) => prod.no === productNo);

        return currentProduct.description;

    }

    const statusClass = (status) => {
        switch(status) {
            case "Pending":
                return (
                    <span className="relative inline-block px-3 py-1 font-semibold text-orange-900 leading-tight">
                        <span aria-hidden className="absolute inset-0 bg-orange-200 opacity-50 rounded-full"></span>
                        <span className="relative">{status}</span>
                    </span>
                );
            case "Rejected":
                return (
                    <span className="relative inline-block px-3 py-1 font-semibold text-red-900 leading-tight">
                        <span aria-hidden className="absolute inset-0 bg-red-200 opacity-50 rounded-full"></span>
                        <span className="relative">{status}</span>
                    </span>
                );
            default:
                return (
                    <span className="relative inline-block px-3 py-1 font-semibold text-green-900 leading-tight">
                        <span aria-hidden className="absolute inset-0 bg-green-200 opacity-50 rounded-full"></span>
                        <span className="relative">{status}</span>
                    </span>
                );

        }

    }

    if (isLoading || isProductLoading) {
        return (
        <>
            <div className="w-full m-auto px-8 relative">
            <img
                src="/img/loading-gif.gif"
                alt=""
                className="w-1/8 m-auto py-20"
            />
            </div>
        </>
        );
    }

    return (
        <>
        <main className="md:pl-12 set-bg">
            <div className="flex ">
                <div className="w-full ">
                    <div className="flex items-center mt-10 lg:mt-0">
                        <div className="w-8 h-1 bg-blue-700 rounded mr-0 hidden md:block"></div>
                        <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-normal md:font-semibold text-md md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                            My Applications
                        </h4>
                    </div>
                    <br />

                    <div className="w-full md:w-11/12 bg-none md:bg-white flex rounded py-0 md:py-4 shadow-none md:shadow-lg md:border-b-4 md:border-t-8 md:border-blue-600 mb-0 md:mb-20 m-auto md:m-0">
                        <div className="w-full mb-6 px-8 py-6 relative">
                            <h1 className="block text-blue-800 text-lg font-bold mb-6 fade-in">
                                Recent loan applications:
                            </h1>
                            <div className="-mx-4 sm:-mx-8 px-4 sm:px-8 py-0 md:py-4 overflow-auto  fade-in mt-5 mb-10">
                                <div className="inline-block min-w-full shadow rounded-lg overflow-hidden">
                                    <table className="min-w-full w-full leading-normal">
                                        <thead className="flex w-full">
                                            <tr className="flex w-full">
                                                <th className="p-4 w-1/4 px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">Date</th>
                                                <th className="p-4 w-1/4 px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">Loan Type</th>
                                                <th className="p-4 w-1/4 px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider text-center">Amount</th>
                                                <th className="p-4 w-1/4 px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider text-center">Duration</th>
                                                <th className="p-4 w-1/4 px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider text-center">Status</th>
                                            </tr>
                                        </thead>
                                        <tbody className="flex flex-col items-center overflow-y-scroll w-full schedule-table">
                                        {data?.data?.length > 0
                                            ?
                                            data && data.data.map((d) => (
                                                <>
                                                    <tr className="flex w-full">
                                                        <td className="p-4 w-1/4 px-5 py-5 border-b border-gray-200 bg-white text-xs md:text-sm text-gray-700">{ new Date(d.created_at).toDateString()}</td>
                                                        <td className="p-4 w-1/4 px-5 py-5 border-b border-gray-200 bg-white text-xs md:text-sm text-gray-700">{getProductName(d.product_code)}</td>
                                                        <td className="p-4 w-1/4 px-5 py-5 border-b border-gray-200 bg-white text-xs md:text-sm text-green-600 font-bold text-center">â‚¦{Number(d.loan_amount).toLocaleString(undefined, {maximumFractionDigits: 2})}</td>
                                                        <td className="p-4 w-1/4 px-5 py-5 border-b border-gray-200 bg-white text-xs md:text-sm text-gray-700 text-center">{d.number_of_payments} months</td>
                                                        <td className="p-4 w-1/4 px-5 py-5 border-b border-gray-200 bg-white text-xs md:text-sm text-green-600 font-bold text-center">{statusClass(d.status)}</td>
                                                    </tr>
                                                </>
                                            ))
                                            :
                                            null
                                        }

                                        </tbody>
                                    </table>  
                                </div>
                            </div>

                        </div>
                    </div>
                    <div className="w-11/12 mt-8 hidden md:block">
                        <hr className="w-9/12 border-t-2 border-blue-600 bg-blue-400 rounded m-auto"></hr>
                    </div>
                </div>
            </div>
            <br />
        </main>
        </>
    )
}

export default withRouter(LoanRequests);
