        <FocusLock isLocked={props.isFocusLock} isOpen={isOpen}>
            <div
                ref={overlayElement}
                className={[
                    styles.overlay,
                    !isOpen ? styles.overlayHidden : '',
                    isCompletelyClose && !isOpen && props.isStatic ? styles.staticDialog : '',
                ].join(' ')}
