    scrollBarPlaceholderColor?: string;

    onAfterOpen?: () => void;
}

export async function CustomDialog<T>(
    body: JSX.Element,
    options?: CustomConfig
): Promise<T | undefined> {
    return showModal<T>(Dialog, {
        ...options,
        children: body,
        isOpen: true,
    });
}
