import React, { useState } from "react";
import { useQuery } from "react-query";
import { withRouter } from "react-router";
import OnboardingSideBar from "../components/OnboardingSideBar";
import {useForm} from "react-hook-form";

import { getBvn, validateOTP } from "../clients/bvn";


const Bvn =(props:any)=> {

  const [loading, setLoading] = useState(false)
  const [displayMsg, setDisplayMsg] = useState("")
  const [otpMsg, setOtpMsg] = useState(null)

  const { register, setValue, handleSubmit, errors } = useForm();

  const onOtpSubmit = async ()=>{
    console.log('no')
         let data = await validateOTP();
         console.log(data)
         setOtpMsg(data.message);
         setTimeout(
          function() {
              setOtpMsg(null );
              props.history.push('/onboarding/personal-details')
          },
          2000
         );
  }

  const onSubmit = async (datas : any)=>{
    setLoading(true)
    let data = null;
    console.log('yes')
    try {
      setLoading(false)
      setValue("bvn", "")
      setValue("otp", "")
    //   if (displayMsg) {
    // console.log('no')
    //      data = await validateOTP();
    //      console.log(data)
    //      setOtpMsg(data.message);
    //      setTimeout(
    //       function() {
    //           setOtpMsg(null );
    //           props.history.push('/onboarding/personal-details')
    //       },
    //       2000
    //      );
    //   }
       data = await getBvn();
       let mobile = data.data.mobile.split("")
       let phone = "xxx-xxx-xxx-"+mobile[9]+mobile[10]
       let message = data.message + " " + phone

      setDisplayMsg(message)
    } catch (error) {
      console.log(error)
    }
  }



  return (
    <>
      {/* Desktop view */}
      <div className="hidden md:block w-5/12 mb-6 border-r-2 border-gray-200">
        <OnboardingSideBar name="BVN" />
      </div>

      <div className="w-full md:w-7/12 mb-6 px-8 relative">
        <h1 className="block text-gray-700 text-lg font-bold mb-2">
          BVN DETAILS
        </h1>
          {
            otpMsg ?
            <div className="bg-teal-100 border-t-4 border-teal-500 rounded-b text-teal-900 px-4 py-3 shadow-md" role="alert">
              <div className="flex">
                <div className="py-1"><svg className="fill-current h-6 w-6 text-teal-500 mr-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M2.93 17.07A10 10 0 1 1 17.07 2.93 10 10 0 0 1 2.93 17.07zm12.73-1.41A8 8 0 1 0 4.34 4.34a8 8 0 0 0 11.32 11.32zM9 11V9h2v6H9v-4zm0-6h2v2H9V5z"/></svg></div>
                <div>
                  <p className="font-bold">Sent successfully</p>
                  <p className="text-sm">{otpMsg}</p>
                </div>
              </div>
            </div>
            :
            <>
        <h3 className="text-gray-600 text-sm">{displayMsg?displayMsg:"We hate to ask, but regulations mandate us to. <br/> Your BVN is safe with us!"} </h3>
        <form className="rounded pt-10" onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-4 w-11/12">

            {displayMsg?
            <>
            <input name="otp" ref={register({ required: true })}  className="shadow appearance-none border rounded w-full py-3 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Enter the OTP sent to your phone..." />
            <div className="flex items-center justify-between mb-24 mt-24 bottom-40 ">
                <button onClick={onOtpSubmit} disabled={loading} className="bg-blue-700 hover:bg-blue-800 text-white text-xl font-light py-3 px-10 rounded focus:outline-none focus:shadow-outline inline-flex items-center" type="button">
                  <span> Validate </span>
                  <svg className="ml-2"  width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <g id="done_24px">
                    <path id="icon/action/done_24px" d="M6.5999 11.925L3.4499 8.775L2.3999 9.825L6.5999 14.025L15.5999 5.025L14.5499 3.975L6.5999 11.925Z" fill="white"/>
                    </g>
                  </svg>
                </button>

              </div>
            </>
             :
             <>
              <input name="bvn" ref={register({ required: true, minLength: 11 })} className="shadow appearance-none border rounded w-full py-3 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="number"  placeholder="Enter your 11-digit BVN..." />
              {errors.bvn &&  errors.bvn.type === "required" && (<p className="text-red-500 text-xs italic">Please fill out this field.</p>)}
              {errors.bvn &&  errors.bvn.type === "minLength" && (<p className="text-red-500 text-xs italic">Minimum BVN length is 11.</p>)}

              <div className="flex items-center justify-between mb-24 mt-24 bottom-40 ">
                <button disabled={loading} className="bg-blue-700 hover:bg-blue-800 text-white text-xl font-light py-3 px-10 rounded focus:outline-none focus:shadow-outline inline-flex items-center" type="submit">
                  <span> Validate </span>
                  <svg className="ml-2"  width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <g id="done_24px">
                    <path id="icon/action/done_24px" d="M6.5999 11.925L3.4499 8.775L2.3999 9.825L6.5999 14.025L15.5999 5.025L14.5499 3.975L6.5999 11.925Z" fill="white"/>
                    </g>
                  </svg>
                </button>

              </div>
            </>
            }
          </div>


        </form>
            </>
          }
           <div className="absolute bottom-0 right-0 mr-16 -mt-8">
            <button disabled onClick={()=>props.history.push('/onboarding/personal-details')} className="bg-gray-400 hover:bg-gray-600 text-white text-xl font-light py-3 px-10 rounded focus:outline-none focus:shadow-outline inline-flex items-center" type="button">
                <span> Next </span>
                <svg className="ml-2"  width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <g id="done_24px">
                  <path id="icon/action/done_24px" d="M6.5999 11.925L3.4499 8.775L2.3999 9.825L6.5999 14.025L15.5999 5.025L14.5499 3.975L6.5999 11.925Z" fill="white"/>
                  </g>
                </svg>
              </button>
            </div>
      </div>

      {/* End of Desktop View */}
    </>
  );
};

export default withRouter(Bvn);
