import React, { useEffect, useCallback } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Keycloak from "keycloak-js";

import Layout from "./components/Layout";
import Home from "./pages/Home";
import Profile from "./pages/Profile";
import Onboarding from "./pages/Onboarding";
import Bvn from "./pages/Bvn";
import useAuthStore from "./stores/authStore";
import MyLoans from "./pages/MyLoans";
import NewLoanRequest from "./pages/NewLoanRequest";
import LoanSchedule from "./pages/LoanSchedule";
import LoanApplication from "./pages/LoanApplication";

import { getPersonalDetails } from "./clients/profile";

import {} from "./stores/authStore";

export default function App() {
  const authenticated = useAuthStore(
    useCallback((state) => state.authenticated, [])
  );

  const initializeAuthStore = useAuthStore(
    useCallback((state) => state.initialize, [])
  );

  const _initializeAuthentication = useCallback(() => {
    const keycloak = Keycloak({
      realm: process.env.REACT_APP_AUTH_REALM ?? "",
      url: process.env.REACT_APP_AUTH_SERVER_URL ?? "",
      clientId: process.env.REACT_APP_AUTH_CLIENT_ID ?? "",
    });

    keycloak.init({ onLoad: "login-required" }).then((authenticated) => {
      initializeAuthStore(keycloak);
      const token = useAuthStore((state) => state.accessToken);
      console.log(token);
    });
  }, [initializeAuthStore]);

  useEffect(() => {
    _initializeAuthentication();
    return () => {};
  }, [_initializeAuthentication]);

  if (!authenticated) {
    return <div>One moment...</div>;
  }

  return (
    <Router>
      <Layout>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/new-loan-request">
            <NewLoanRequest />
          </Route>
          <Route path="/loan-schedule">
            <LoanSchedule />
          </Route>
          <Route path="/profile">
            <Profile />
          </Route>
          <Route path="/loans">
            <MyLoans />
          </Route>
          <Route path="/onboarding">
            <Onboarding />
          </Route>
          <Route path="/application">
            <LoanApplication />
          </Route>
        </Switch>
      </Layout>
    </Router>
  );
}
