import axios from "axios";

export default function apiHandler(token = null, file = null) {
  return axios.create({
    baseURL: process.env.REACT_APP_APP_BASE_URL,
    ...(token
      ? {
          headers: {
            Authorization: `Bearer ${token}`,
            "Access-Control-Allow-Origin": "*",
          },
        }
      : file
      ? {
          Authorization: `Bearer ${token}`,
        }
      : {}),
  });
}
