import React, { Fragment } from "react";
import { useTable } from "react-table";
import ReactDatatable from "@ashvin27/react-datatable";
import { useParams } from "react-router-dom";
import { getLoan } from "../clients/loan";
import { useQuery } from "react-query";
import useAuthStore from "../stores/authStore";
import { withRouter } from "react-router";

// import 'bootstrap/dist/css/bootstrap.css';

function LoanDetails(props) {
  const token = useAuthStore((state) => state.accessToken);

  let { loanId } = useParams();
  const { isLoading, error, data: loan } = useQuery("loanDetail", () =>
    getLoan(token, loanId)
  );

  const statusClass = (status) => {
    switch (status) {
      case "Pending":
        return (
          <span className="relative inline-block px-3 py-1 font-semibold text-orange-900 leading-tight">
            <span
              aria-hidden
              className="absolute inset-0 bg-orange-200 opacity-50 rounded-full"
            ></span>
            <span className="relative">{status}</span>
          </span>
        );
      case "Rejected":
        return (
          <span className="relative inline-block px-3 py-1 font-semibold text-red-900 leading-tight">
            <span
              aria-hidden
              className="absolute inset-0 bg-red-200 opacity-50 rounded-full"
            ></span>
            <span className="relative">{status}</span>
          </span>
        );
      default:
        return (
          <span className="relative inline-block px-3 py-1 font-semibold text-green-900 leading-tight">
            <span
              aria-hidden
              className="absolute inset-0 bg-green-200 opacity-50 rounded-full"
            ></span>
            <span className="relative">{status}</span>
          </span>
        );
    }
  };

  let loanData = loan && loan.data;
  let loanDatas = [] as any;

  loan &&
    loanData["schedules"].map((item) => {
      loanDatas.push({
        col1: item.id,
        col2: (
          <span className="text-green-600 font-bold">
            ₦
            {Number(item.repayment).toLocaleString(undefined, {
              maximumFractionDigits: 2,
            })}
          </span>
        ),
        col3: new Date(item.schedule_date).toDateString(),
        col4: statusClass(item.payment_status),
      });
    });

  const data = React.useMemo(
    () => [
      // {
    ],
    []
  );

  const columns = React.useMemo(
    () => [
      {
        Header: "S/N",
        accessor: "col1", // accessor is the "key" in the data
      },
      {
        Header: "Opening Balance",
        accessor: "col2",
      },
      {
        Header: "Date",
        accessor: "col3",
      },
      {
        Header: "Status",
        accessor: "col4",
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({ columns: columns, data: loanDatas });

  if (isLoading) {
    return (
      <>
        <div className="w-full m-auto px-8 relative">
          <img
            src="/img/loading-gif.gif"
            alt=""
            className="w-1/8 m-auto py-20"
          />
        </div>
      </>
    );
  }

  return (
    <>
      <main className="md:pl-12">
        <div className="flex ">
          <div className="w-full ">
            <div className="flex items-center mt-10 lg:mt-0">
              <div className="w-8 h-1 bg-blue-700 rounded mr-0 hidden md:block"></div>
              <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                Loan Details
              </h4>
            </div>
            <br />
            <div className="w-11/12 bg-white flex flex-row rounded py-6 shadow-lg border-b-4 border-blue-700 md:border-none pl-4 mb-20 m-auto md:m-0">
              <div className="w-1/4  border-r-8 border-blue-100 align-middle justify-center">
                <h2 className="text-blue-700 md:text-4xl font-semibold py-5 pl-3 sm:text-2xl">
                  {loanData.loan_id}
                </h2>
              </div>
              <div className="w-1/4 flex-row">
                <div className="flex flex-row text-blue-700 text-lg mb-4">
                  <div className="w-1/2 text-right mr-5 md:text-lg sm:text-sm">
                    Amount:{" "}
                  </div>
                  <div className="w-1/2 text-left font-bold">
                    ₦
                    {Number(loanData.loan_amount).toLocaleString(undefined, {
                      maximumFractionDigits: 2,
                    })}
                  </div>
                </div>
                <div className="flex flex-row text-blue-700 md:text-lg sm:text-sm">
                  <div className="w-1/2 text-right mr-5 ">Duration:</div>
                  <div className="w-1/2 text-left font-bold">
                    {loanData.number_of_payments} months
                  </div>
                </div>
              </div>
              <div className="w-1/4 flex flex-col ">
                <div className="w-full mb-4">
                  <p
                    style={{ fontSize: 8 }}
                    className="text-center w-1/3 float-right text-teal-600 bg-teal-100 py-2  rounded-full"
                  >
                    {loanData.status}
                  </p>
                </div>

                <div className="flex flex-row text-blue-700 text-lg">
                  <p className="w-1/2 text-right mr-5">Repayment</p>
                  <p className="w-1/2 text-right font-bold">
                    ₦
                    {loanData["schedules"].length
                      ? Number(
                          loanData["schedules"][0]["repayment"]
                        ).toLocaleString(undefined, {
                          maximumFractionDigits: 2,
                        })
                      : ""}
                  </p>
                </div>
              </div>
              <div
                style={{ alignItems: "center" }}
                className="w-1/4  flex justify-end align-middle "
              >
                <button
