import React from "react";
import { useQuery } from "react-query";
import { withRouter } from "react-router";
import OnboardingSideBar from "../components/OnboardingSideBar";
import {useForm} from "react-hook-form";

import { getBvn } from "../clients/bvn";


const Bvn =(props:any)=> {

  const { register, handleSubmit, errors } = useForm();

  const { isLoading, error, data } = useQuery(() => getBvn());

  const onSubmit =(data : any)=>{

  }


  return (
    <>

    {/* Desktop view */}
    <>
      <div className="w-5/12 mb-6 border-r-2 border-gray-200">
          <OnboardingSideBar name="BVN" />
          <img src="/img/checklists.svg" alt="" className="w-1/4 m-auto pt-16"/>
      </div>

      <div className="w-7/12 mb-6 px-8 ">
        <h1 className="block text-gray-700 text-lg font-bold mb-2">
              BVN DETAILS
        </h1>
        <h3 className="text-gray-600 text-sm"> We hate to ask, but regulations mandate us to. <br/> Your BVN is safe with us!</h3>
        <form className="rounded pt-10" onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-4 w-11/12">
            <input name="bvn" ref={register({ required: true, minLength: 11 })} className="shadow appearance-none border rounded w-full py-3 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="number" placeholder="Enter your 11-digit BVN..." />
            {errors.bvn &&  errors.bvn.type === "required" && (<p className="text-red-500 text-xs italic">Please fill out this field.</p>)}
            {errors.bvn &&  errors.bvn.type === "minLength" && (<p className="text-red-500 text-xs italic">Minimum BVN length is 11.</p>)}
          </div>
          <div className="flex items-center justify-between mb-24">
              <button className="bg-blue-700 hover:bg-blue-800 text-white text-xl font-light py-3 px-10 rounded focus:outline-none focus:shadow-outline inline-flex items-center" type="submit">
                <span> Validate </span>
                <svg className="ml-2"  width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <g id="done_24px">
                  <path id="icon/action/done_24px" d="M6.5999 11.925L3.4499 8.775L2.3999 9.825L6.5999 14.025L15.5999 5.025L14.5499 3.975L6.5999 11.925Z" fill="white"/>
                  </g>
                </svg>
              </button>
            </div>
            <div className="flex justify-end">
            <button onClick={()=>props.history.push('/onboarding/personal-details')} className="bg-gray-400 hover:bg-gray-600 text-white text-xl font-light py-3 px-10 rounded focus:outline-none focus:shadow-outline inline-flex items-center" type="button">
                <span> Next </span>
                <svg className="ml-2"  width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <g id="done_24px">
                  <path id="icon/action/done_24px" d="M6.5999 11.925L3.4499 8.775L2.3999 9.825L6.5999 14.025L15.5999 5.025L14.5499 3.975L6.5999 11.925Z" fill="white"/>
                  </g>
                </svg>
              </button>
            </div>
        </form>
      </div>
    </>         

    {/* End of Desktop View */}
    </>
  );
}

export default withRouter(Bvn);
