import React, { useState } from "react";
import { useQuery } from "react-query";
import { withRouter } from "react-router";
import OnboardingSideBar from "../components/OnboardingSideBar";
import { useForm } from "react-hook-form";

import { getBvn, validateOTP } from "../clients/bvn";
import {
  resolveBvn,
  getBvnDetailsFields,
  completeResolveBVN,
} from "../clients/onboarding";
import useAuthStore from "../stores/authStore";
import FormInput from "../components/FormInput";

const Bvn = (props: any) => {
  const [loading, setLoading] = useState(false);
  const [displayMsg, setDisplayMsg] = useState("");
  const [errorMsg, setErrorMsg] = useState(null);
  const [otpMsg, setOtpMsg] = useState(null);
  const [userInput, setUserInput] = useState(null);
  const [otpRequestId, setOtpRequestId] = useState(null);
  const [otpError, setOtpError] = useState(false);
  const token = useAuthStore((state) => state.accessToken);

  const { isLoading, error, data } = useQuery("data", () =>
    getBvnDetailsFields(token)
  );

  const { register, setValue, handleSubmit, errors } = useForm();

  if (isLoading) {
    return <div>Loading...</div>;
  }

  const onOtpSubmit = async () => {
    // if (otpNumber != userInput) {
    //   setOtpError(true);
    //   return;
    // }

    // console.log("no");
    try {
      let otp = {};
      otp["otp"] = userInput;
      otp["requestid"] = otpRequestId;
      otp["user_id"] = "13445";
      let data = await completeResolveBVN(token, otp);
      setOtpMsg(data.message);
    } catch (error) {
      console.log(error);
    }
    // let data = await validateOTP();
    // console.log(data);
    // setTimeout(function () {
    //   setOtpMsg(null);
    //   props.history.push("/onboarding/personal");
    // }, 2000);
  };

  const setUsersInput = (e: any) => {
    setUserInput(e.target.value);
  };

  const onSubmit = async (datas: any) => {
    setLoading(true);
    // let data = null;
    // console.log("yes");
    // try {
    //   setLoading(false);
    //   setValue("bvn", "");
    //   setValue("otp", "");

    //   let data = await getBvn();
    //   let mobile = data.data.mobile.split("");
    //   setOtpNumber(data.data.otprequestid);
    //   let phone = "xxx-xxx-xxx-" + mobile[9] + mobile[10];
    //   let message = data.message + " " + phone;

    //   setDisplayMsg(message);
    // } catch (error) {
    //   console.log(error);
    // }
    try {
      let values = datas;
      values.user_id = "13445";
      let postData = await resolveBvn(token, values);
      const { message, data, success } = postData;

      if (success === false) {
        setErrorMsg(message);
      }
      let mobile = data.mobile.split("");
      let phone = ", xxx-xxx-xxx-" + mobile[9] + mobile[10];
      let Displaymessage = message + " " + phone;

      setOtpRequestId(data.otprequestid);
      setLoading(false);
      setDisplayMsg(Displaymessage);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <>
      <div className="hidden md:block w-5/12 mb-6 border-r-2 border-gray-200">
        <OnboardingSideBar name="bvn" />
      </div>

      <div className="w-full md:w-7/12 mb-6 px-8 relative">
        <h1 className="block text-gray-700 text-lg font-bold mb-2">
          BVN DETAILS
        </h1>
        {errorMsg ? (
          <div
            className="bg-red-600 border-t-4 border-teal-500 rounded-b text-teal-900 px-4 py-3 shadow-md"
            role="alert"
          >
            <div className="flex">
              <div className="py-1">
                <svg
                  className="fill-current h-6 w-6 text-teal-500 mr-4"
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 20 20"
                >
                  <path d="M2.93 17.07A10 10 0 1 1 17.07 2.93 10 10 0 0 1 2.93 17.07zm12.73-1.41A8 8 0 1 0 4.34 4.34a8 8 0 0 0 11.32 11.32zM9 11V9h2v6H9v-4zm0-6h2v2H9V5z" />
                </svg>
              </div>
              <div>
                {/* <p className="font-bold">Sent successfully</p> */}
                <p className="text-sm">{errorMsg}</p>
              </div>
            </div>
          </div>
        ) : (
          ""
        )}
        {otpMsg ? (
          <div
            className="bg-teal-100 border-t-4 border-teal-500 rounded-b text-teal-900 px-4 py-3 shadow-md"
            role="alert"
          >
            <div className="flex">
              <div className="py-1">
                <svg
                  className="fill-current h-6 w-6 text-teal-500 mr-4"
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 20 20"
                >
                  <path d="M2.93 17.07A10 10 0 1 1 17.07 2.93 10 10 0 0 1 2.93 17.07zm12.73-1.41A8 8 0 1 0 4.34 4.34a8 8 0 0 0 11.32 11.32zM9 11V9h2v6H9v-4zm0-6h2v2H9V5z" />
                </svg>
              </div>
              <div>
                {/* <p className="font-bold">Sent successfully</p> */}
                <p className="text-sm">{otpMsg}</p>
              </div>
            </div>
          </div>
        ) : (
          <>
            <h3 className="text-gray-600 text-sm">
              {displayMsg
                ? displayMsg
                : "We hate to ask, but regulations mandate us to.\n Your BVN is safe with us!"}{" "}
            </h3>
            <form className="rounded pt-10" onSubmit={handleSubmit(onSubmit)}>
              <div className="flex flex-wrap -mx-3 mb-6">
                {data &&
                  data.map((data, key) => {
                    return (
                      <FormInput
                        key={key}
                        label="Bank Verification No. (BVN)"
                        name="bvn"
                        required={data.required}
                        errors={errors}
                        register={register}
                        type={data.type}
                        options={data.options}
                        value={null}
                      />
                    );
                  })}

                {displayMsg ? (
                  <>
                    <input
                      name="otp"
                      onInput={setUsersInput}
                      className="shadow appearance-none border rounded w-full py-3 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      type="text"
                      placeholder="Enter the OTP sent to your phone..."
                    />
                    {otpError ? (
                      <p className="text-red-500 text-xs italic">
                        Incorrect OTP
                      </p>
                    ) : (
                      ""
                    )}
                    <div className="flex items-center justify-between mb-24 mt-6 bottom-40 ">
                      <button
                        onClick={onOtpSubmit}
                        disabled={loading}
                        className="bg-blue-700 hover:bg-blue-800 text-white text-sm font-light py-3 px-10 rounded focus:outline-none focus:shadow-outline inline-flex items-center"
                        type="button"
                      >
                        <span> Validate </span>
                        <svg
                          className="ml-2"
                          width="18"
                          height="18"
                          viewBox="0 0 18 18"
                          fill="none"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <g id="done_24px">
                            <path
