import React, { useState, useEffect, useCallback } from "react";
import OnboardingSideBar from "../components/OnboardingSideBar";
import { useForm, Controller } from "react-hook-form";

import { useQuery } from "react-query";

import useProfileStore from "../stores/profileStore";
import { getPersonalDetails } from "../clients/profile";
import useAuthStore from "../stores/authStore";
import {
  getPersonalDetailsFields,
  patchRequest,
  getProfileSection,
} from "../clients/onboarding";
import FormInput from "../components/FormInput";

const PersonalDetails = (props: any) => {
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState([]);
  const profile = useProfileStore((state) => state.data);
  const token = useAuthStore((state) => state.accessToken);
  const [readonlyValue, setReadonlyValue] = useState(false);
  const { isLoading, error, data } = useQuery("personaldetails", () =>
    getPersonalDetailsFields(token)
  );

  const {
    isLoading: isLoading2,
    error: error2,
    data: profileSection,
  } = useQuery("sections", () => getProfileSection(token));

  const getUpdatedProfile = async () => {
    let getProfile = await getPersonalDetails(token);
    initializeProfileStore(getProfile.data);
  };

  useEffect(() => {
    getUpdatedProfile();
  }, []);

  const initializeProfileStore = useProfileStore(
    useCallback((state) => state.initialize, [])
  );

  const initializeProfileSection = useProfileStore(
    useCallback((state) => state.initializeProfileSection, [])
  );

  useEffect(() => {
    profileSection &&
      initializeProfileSection(
        profileSection.reverse(),
        profileSection.every((loan) => loan.completed === true)
      );
    console.log(profileSection && profileSection.reverse());
  }, [profileSection]);

  const {
    register,
    handleSubmit,
    errors,
    reset,
    control,
    unregister,
  } = useForm();
  const [submitted, setSubmitted] = useState(false);
  // console.log(profile["user_id"]);

  if (isLoading || isLoading2) {
    return (
      <>
        <div className="w-full m-auto px-8 relative">
          <img
            src="/img/loading-gif.gif"
            alt=""
            className="w-1/8 m-auto py-20"
          />
        </div>
      </>
    );
  }

  const toggleInput = () => {
    setReadonlyValue((prev) => !prev);
  };

  const updateErrors = (errors: []) => {
    setErrorMsg(Object.values(errors).flat());
  };

  const onSubmit = async (values: any, e) => {
    let value = {};
    data.map((val) => {
      value[val.name] = values[val.name];
    });
    setLoading(true);
    try {
      let postData = await patchRequest(token, value);
      e.target.reset();
      setSubmitted(true);
      setLoading(false);
      const { data } = postData;
      initializeProfileStore(data);
      setErrorMsg([]);
      setTimeout(function () {
        setSubmitted(false);
        props.history.push("/onboarding/next_of_kin");
      }, 2000);
    } catch (error) {
      setLoading(false);
      if (error && error.response.status === 500) {
        let msg = [] as any;
        msg.push(error.response.data.message);
        setErrorMsg(msg);
        return;
      }
      if (!error.response) {
        let msg = [] as any;
        msg.push("Network Error, Check Your Internet Connection");
        setErrorMsg(msg);
        return;
      }
      const {
        response: { data },
      } = error;
      data.errors && updateErrors(data.errors);
    }
  };
  console.log("PersonalDetails");
  return (
    <>
      <div className="hidden md:block w-5/12 mb-6 border-r-2 border-gray-200">
        <OnboardingSideBar name="personal" />
        <img src="/img/checklists.svg" alt="" className="w-1/4 m-auto pt-16" />
      </div>

      <div className="md:w-7/12 w-full mb-6 px-8 relative">
        <h1 className="block text-gray-700 text-lg font-bold mb-4">
          PERSONAL DETAILS
        </h1>
        {errorMsg.length ? (
          <div
            className="bg-red-100 border-t-4 border-red-500 rounded-b text-red-900 px-4 py-3 shadow-md my-5"
            role="alert"
          >
            <div className="flex">
              <div className="py-1">
                <svg
                  className="fill-current h-6 w-6 text-red-500 mr-4"
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 20 20"
                >
                  <path d="M2.93 17.07A10 10 0 1 1 17.07 2.93 10 10 0 0 1 2.93 17.07zm12.73-1.41A8 8 0 1 0 4.34 4.34a8 8 0 0 0 11.32 11.32zM9 11V9h2v6H9v-4zm0-6h2v2H9V5z" />
                </svg>
              </div>
              <div>
                {/* <p className="font-bold">Sent successfully</p> */}
                {/* <p className="text-sm">{errorMsg}</p> */}
                <ul style={{ listStyleType: "none" }}>
                  {errorMsg.map((error, i) => (
                    <li key={i}>{error}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        ) : (
          ""
        )}
        {/* <h3 className="text-gray-600 text-sm"> We hate to ask, but regulations mandate us to. <br/> Your BVN is safe with us!</h3> */}
        {submitted ? (
          <>
            <div
              className="bg-teal-100 border-t-4 border-teal-500 rounded-b text-teal-900 px-4 py-3 shadow-md"
              role="alert"
            >
              <div className="flex">
                <div className="py-1">
                  <svg
                    className="fill-current h-6 w-6 text-teal-500 mr-4"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 20 20"
                  >
                    <path d="M2.93 17.07A10 10 0 1 1 17.07 2.93 10 10 0 0 1 2.93 17.07zm12.73-1.41A8 8 0 1 0 4.34 4.34a8 8 0 0 0 11.32 11.32zM9 11V9h2v6H9v-4zm0-6h2v2H9V5z" />
                  </svg>
                </div>
                <div>
                  <p className="font-bold">Sent successfully</p>
                  <p className="text-sm">
                    Personal details created successfully
                  </p>
                </div>
              </div>
            </div>
          </>
        ) : (
          <>
            <form className="w-full max-w-lg" onSubmit={handleSubmit(onSubmit)}>
              <div className="flex flex-wrap -mx-3 mb-6">
                {data &&
                  data.map((data, key) => {
                    return (
                      <FormInput
                        key={key}
                        label={data.label}
                        name={data.name}
                        required={data.required}
                        errors={errors}
                        register={register}
                        type={data.type}
                        options={data.options}
                        value={profile[data.name]}
                        Controller
                        control
