                  data.map((data, key) => {
                    return (
                      <FormInput
                        key={key}
                        label={data.label}
                        name={data.name}
                        required={data.required}
                        errors={errors}
                        register={register}
                        type={data.type}
                        options={data.options}
                        value={profile[data.name]}
                      />
                    );
                  })}
                {/* {displayMsg ? (
                  <>
                    <input
                      name="otp"
                      onInput={setUsersInput}
