import React, { useEffect, useState } from "react";
import { useQuery } from "react-query";
import { NavLink, useHistory, Route, Switch } from "react-router-dom";
import { withRouter } from "react-router";
import Bvn from "./Bvn";
import PersonalDetails from "./PersonalDetails";
import EducationalDetails from "./EducationalDetails";
import OnboardingSideBar from "../components/OnboardingSideBar";
import AccountDetails from "./AccountDetails";
import OnboardingLoanRequest from "./OnboardingLoanRequest";
import Employment from "./Employment";




function Onboarding(props: any) {

  const history = useHistory();

  // const [cardBackgroud, setCardBackground] = useState("");

  // useEffect(() => {
  //   setCardBackground(_cardBackgroundPath(history.location.pathname))

  //   return () => {};
  // }, [setCardBackground, history])

  // function _cardBackgroundPath(route: string) {
  //   let imgMap = new Map<string, string>([
  //     ['onboarding', '/img/bvn-mobile-card-bg.svg']
  //   ]);

  //   return imgMap.get(route) ?? "";
  // }

  return (
    <>
      <main className="md:pl-12">
        <div className="flex ">
          <div className="w-full ">
            <div className="flex items-center mt-10 lg:mt-0">
              <div className="w-8 h-1 bg-blue-700 rounded mr-3 hidden md:block"></div>
              <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                Profile Update
              </h4>
            </div>
            <br />
            <img src="/img/bvn-mobile-card-bg.svg" alt=""  className="md:hidden absolute mobile-card-bg"/>
            <div className="relative w-11/12 bg-white flex rounded py-12 shadow-lg border-b-4 border-blue-700 md:border-none mb-20 m-auto md:m-0 mt-12 md:mt-0">
              <Switch>
                <Route
                  path="/onboarding/onboarding-loan-request"
                  component={OnboardingLoanRequest}
                />
                <Route
                  path="/onboarding/employment"
                  component={Employment}
                />
                <Route
                  path="/onboarding/account-details"
                  component={AccountDetails}
                />
                <Route
                  path="/onboarding/personal-details"
                  component={PersonalDetails}
                />
                <Route
                  path="/onboarding/education"
                  component={EducationalDetails}
                />
                <Route path="/" component={Bvn} />
              </Switch>
            </div>
            <div className="w-11/12 mt-8 hidden md:block">
              <hr className="w-9/12 border-t-2 border-blue-600 bg-blue-400 rounded m-auto"></hr>
            </div>
          </div>
        </div>
        <br />
      </main>

    </>
  );
}

export default withRouter(Onboarding);
