import React, {Fragment} from "react";
import { useQuery } from "react-query";
import ReactDatatable from '@ashvin27/react-datatable';

function MyLoans() {

  const columns = [
    {
        key: "id",
        text: "Loan Id",
        className: "px-4 py-3 text-xs font-semibold text-gray-700",
        sortable: true,
    },
    {
        key: "address",
        text: "Type",
        className: "px-4 py-3 text-xs font-semibold text-gray-700",
        sortable: true
    },
    {
        key: "postcode",
        text: "Amount",
        className: "px-4 py-3 text-xs font-semibold text-gray-700",
        sortable: true
    },
    {
        key: "rating",
        text: "Date",
        className: "px-4 py-3 text-xs font-semibold text-gray-700",
        sortable: true
    },
    {
        key: "type_of_food",
        text: "Type of Food",
        className: "px-4 py-3 text-xs font-semibold text-gray-700",
        sortable: true,
    },
    {
        key: "action",
        text: "Action",
        className: "px-4 py-3 text-xs font-semibold text-gray-700",
        width: 100,
        align: "left",
        sortable: false,
        cell: record => { 
            return (
                <>
                    <button
                        className="btn btn-primary btn-sm"
                        onClick={() => editRecord(record)}
                        style={{marginRight: '5px'}}>
                        <svg width="15" height="15" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <g id="visibility_24px">
                        <path id="icon/action/visibility_24px" fill-rule="evenodd" clip-rule="evenodd" d="M0.625 7.5C1.70625 4.75625 4.375 2.8125 7.5 2.8125C10.625 2.8125 13.2937 4.75625 14.375 7.5C13.2937 10.2438 10.625 12.1875 7.5 12.1875C4.375 12.1875 1.70625 10.2438 0.625 7.5ZM13.0125 7.5C11.9813 5.39375 9.86878 4.0625 7.50003 4.0625C5.13128 4.0625 3.01878 5.39375 1.98753 7.5C3.01878 9.60625 5.13128 10.9375 7.50003 10.9375C9.86878 10.9375 11.9813 9.60625 13.0125 7.5ZM7.5 5.9375C8.3625 5.9375 9.0625 6.6375 9.0625 7.5C9.0625 8.3625 8.3625 9.0625 7.5 9.0625C6.6375 9.0625 5.9375 8.3625 5.9375 7.5C5.9375 6.6375 6.6375 5.9375 7.5 5.9375ZM4.6875 7.5C4.6875 5.95 5.95 4.6875 7.5 4.6875C9.05 4.6875 10.3125 5.95 10.3125 7.5C10.3125 9.05 9.05 10.3125 7.5 10.3125C5.95 10.3125 4.6875 9.05 4.6875 7.5Z" fill="white"/>
                        </g>
                        </svg>
                    </button>
                    <button 
                        className="btn btn-danger btn-sm" 
                        onClick={() => deleteRecord(record)}>
                        <i className="fa fa-trash"></i>
                    </button>
                </>
            );
        }
    }
];

const config = {
  page_size: 10,
  length_menu: [ 10, 20, 50 ],
  button: {
      excel: true,
      print: true,
      extra: true,
  }
}

const records = [
  {
    "id": "55f14312c7447c3da7051b26",
    "address": "228 City Road",
    "name": ".CN Chinese",
    "postcode": "3JH",
    "rating": 5,
    "type_of_food": "Chinese"
  },
  {
    "id": "55f14312c7447c3da7051b27",
    "address": "376 Rayleigh Road",
    "name": "@ Thai",
    "postcode": "5PT",
    "rating": 5.5,
    "type_of_food": "Thai"
  },
  {
    "id": "55f14312c7447c3da7051b28",
    "address": "30 Greyhound Road Hammersmith",
    "name": "@ Thai Restaurant",
    "postcode": "8NX",
    "rating": 4.5,
    "type_of_food": "Thai"
  },
  {
    "id": "55f14312c7447c3da7051b29",
    "address": "30 Greyhound Road Hammersmith",
    "name": "@ Thai Restaurant",
    "postcode": "8NX",
    "rating": 4.5,
    "type_of_food": "Thai"
  }
]

const extraButtons =[
  {
      className:"btn btn-primary buttons-pdf",
      title:"Export TEst",
      children:[
          <span>
          <i className="glyphicon glyphicon-print fa fa-print" aria-hidden="true"></i>
          </span>
      ],
      onClick:(event)=>{
          console.log(event);
      },
  },
  {
      className:"btn btn-primary buttons-pdf",
      title:"Export TEst",
      children:[
          <span>
          <i className="glyphicon glyphicon-print fa fa-print" aria-hidden="true"></i>
          </span>
      ],
      onClick:(event)=>{
          console.log(event);
      },
      onDoubleClick:(event)=>{
          console.log("doubleClick")
      }
  },
]

const editRecord = (record)=> {
  console.log("Edit Record", record);
}

const deleteRecord =(record)=> {
  console.log("Delete Record", record);
}

  return (
      <>
        <main className="md:pl-12">
            <div className="flex ">
                <div className="w-full ">
                    <div className="flex items-center mt-10 lg:mt-0">
                        <div className="w-8 h-1 bg-blue-700 rounded mr-0 hidden md:block"></div>
                        <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                            My Loans
                        </h4>
                    </div>
                    <br />
                    <div className="w-11/12 bg-white flex rounded py-12 shadow-lg border-b-4 border-blue-700 md:border-none mb-20 m-auto md:m-0">

                      <ReactDatatable
                          className="table-fixed sm:w-8/12  m-auto"
                          config={config}
                          records={records}
                          columns={columns}
                          extraButtons={extraButtons}
                      />
                    </div>
                    <div className="w-11/12 mt-8 hidden md:block">
                        <hr className="w-9/12 border-t-2 border-blue-600 bg-blue-400 rounded m-auto"></hr>
                    </div>
                </div>
            </div>
        <br />
        </main>
      </>
  );
}

export default MyLoans;
