import React, { Props, useState } from "react";
import Header from "./Header";
import SideBar from "./SideBar";
import Footer from "./Footer";
import '../assets/custom.css'

function Layout(props: Props<{}>) {
  const [navOpen, setNavopen ] = useState(false)

  const openNav = ()=>{
    setNavopen(preVal=>!preVal);
  }

  return (
    <div  className="w-full m-auto relative h-full lg:flex bg-gray-100   lg:p-0">
      <SideBar navValue={navOpen} />
      <div 
        className={navOpen?"bg-gray-400 bg-opacity-100 z-auto relative":"lg:w-10/12 lg:relative pt-12 lg:pt-0"} 
        onClick={()=>navOpen?openNav():""}>
        <Header navBar = {openNav} />
        <div>{props.children}</div>

        <Footer />
      </div>
    </div>
  );
}

export default Layout;
