import React, { useState, useEffect } from "react";
import { withRouter } from "react-router";

import useAuthStore from "../stores/authStore";
import useProfileStore from "../stores/profileStore";
import { useQuery } from "react-query";
import { getProfileSection } from "../clients/onboarding";

const NewLoanRequest = (props: any) => {
  const [loanStatus, setLoanStatus] = useState();
  //   const profile = useProfileStore((state) => state.profileSection);
  const token = useAuthStore((state) => state.accessToken);

  const { isLoading, error, data } = useQuery("data", () =>
    getProfileSection(token)
  );

  useEffect(() => {
    const loanValue = data && data.every((loan) => loan.completed === true);
    setLoanStatus(loanValue);
  });

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <main className=" lg:block lg:pl-12">
        <div className="flex ">
          <div className="w-full ">
            <div className="flex items-center mt-10 lg:mt-0">
              <div className="w-8 h-1 bg-blue-700 rounded mr-3 hidden md:block"></div>
              <h4 className="bg-blue-700 md:bg-transparent text-white md:text-gray-700 font-light md:font-semibold text-sm md:text-xl tracking-wide uppercase md:normal-case py-2 px-6 rounded-r-lg">
                New Loan
              </h4>
            </div>
            <br />
            <div className="w-11/12 md:bg-white flex-none  rounded py-20 md:shadow-lg mb-20 m-auto md:m-0">
              {loanStatus ? <></> : <></>}
              <div className="w-full m-auto mb-6">
                <img
                  src="img/checklists.svg"
                  alt=""
                  className="w-2/4 md:w-1/4 m-auto"
                />
              </div>
              <div className="w-3/4 md:w-2/4 m-auto">
                <p className="text-gray-700 font-medium text-center">
                  Oops! Looks like you need to complete your profile before we
                  can disburse your money.
                </p>
              </div>
              <div className="w-full m-auto mt-10">
                <button
                  onClick={() => {
                    props.history.push("/onboarding");
                  }}
                  className="flex w-2/4 md:w-1/4 shadow-xl text-xl font-light text-white justify-center py-4 md:py-3 items-center bg-green-600 hover:bg-green-700 rounded-md m-auto"
                >
                  <span className="text-sm xl:text-lg lg:text-sm pr-2">
                    Okay, let's go!
                  </span>
                  <img
                    src="img/chevron-right.svg"
                    className="w-5 pt-1"
                    alt=""
                  />
                </button>
              </div>
            </div>
            <div className="w-11/12 mt-8 hidden md:block">
              <hr className="w-9/12 border-t-2 border-blue-600 bg-blue-400 rounded m-auto"></hr>
            </div>
          </div>
        </div>
        <br />
      </main>
    </>
  );
};

export default withRouter(NewLoanRequest);
